  const toggleHandler = () => {
    setToggle(prevState => !prevState)
  }


  let nums = [4, 5, 2, 3, 8, 4, 6]
  return (
    <div>
      {toggle && <p>Hello</p>}
   <Demo text={"This is demo page"} list={nums} /> 
   <Button onClick={toggleHandler}>Toggle</Button>
   <Button>Delete</Button>
   <Button>Add</Button>
   <Button>Edit</Button>

    </div>
  )
 }
 export default App;
